import { connect } from "react-redux";
import GameNavigatorTop from "../components/main/challenge/GameNavigatorTop"
import { loadNewQuestion, changePopup, skipQuestion, showHint, endGame, setHintCounter } from "../redux/actions"

const mapState = state => ({
  game_is_started: state.game_status.isStarted,
  game_is_loading: state.game_status.isLoading,
  game_database: state.game_database,
  random_game: state.random_game,
  user_score: state.user_score,
})

const mapDispatch  = dispatch => ({
  changePopup: (popup) => dispatch(changePopup(popup)),
  endGame: () => dispatch(endGame()),
  skipQuestion: () => dispatch(skipQuestion()),
  loadNewQuestion: (db) => dispatch(loadNewQuestion(db)),
  startLoading: () => dispatch(startLoading()),
  showHint: () => dispatch(showHint()),
})

export default connect(mapState, mapDispatch)(GameNavigatorTop);
