import styled from "styled-components";
import React from "react"

const CounterContainer = styled.div`
  text-transform: uppercase;
  font-weight: 900;
  font-size: 3rem;
  display: inline-block;
`;

export default function MainCounter({game_status}) {
  const [gameTime, setGameTime] = React.useState(60)

  React.useEffect(() => {
    game_status.isStarted ? console.log('started') : console.log('game over')
  }, [game_status.isStarted])
  React.useEffect(() => {
    game_status.isRunning ? console.log('running') : game_status.isLoading && console.log('loading') 
  }, [game_status.isRunning])
  return (
    <CounterContainer>
      {gameTime}
    </CounterContainer>
  )
}
