import styled from "styled-components";

const ScoreRow = styled.tr`
  font-family: Montserrat;
  font-size: clamp(1.2rem, 2.5vw, 1.6rem);
  letter-spacing: 0.05em;
  line-height: clamp(2rem, 2.5vw, 4rem);
`;

const ScoreColumn = styled.td`
  text-align: center;
`;

export default function ScoresTable({ data }) {
  const scores = data.slice(0, 15);
  return (
    <table>
      <thead>
        <ScoreRow>
          <th>No.</th>
          <th>Name</th>
          <th>Date</th>
          <th>Score</th>
        </ScoreRow>
      </thead>
      <tbody>
        {scores.map((score, i) => (
          <ScoreRow key={i}>
            <ScoreColumn>{i + 1}</ScoreColumn>
            <ScoreColumn>{score.Name}</ScoreColumn>
            <ScoreColumn>{score.Date}</ScoreColumn>
            <ScoreColumn>{score.Score}</ScoreColumn>
          </ScoreRow>
        ))}
      </tbody>
    </table>
  );
}
