import styled from "styled-components"
import React from "react"

const ScreenshotContainer = styled.div`
    background-color: var(--black);
    width: calc(85% - 4rem);
    height: 0;
    padding: 0.5rem;
    padding-bottom: calc(47.75% - (0.5 * 4rem));
    border-radius: 5px;
    box-shadow: 0 0 25px #FFF;
    margin: 0 auto;
    display: flex;

    & > img {
        width: 100%;
        margin: auto;
    }
    `

const screenshots = [
    {
        src: "img/witcher.png",
    },
    {
        src: "img/witcher2.jpg",
    },
    {
        src: "img/witcher3.jpg",
    },
]

export default function Screenshot() { 
const [screenshotIndex, setIndex] = React.useState(2);
    return (
        <ScreenshotContainer>
            <img src={screenshots[screenshotIndex].src} alt="screenshot_img" /> 
        </ScreenshotContainer>
    )
}
