import styled from "styled-components";
import React from "react"

const CounterContainer = styled.div`
  text-transform: uppercase;
  font-weight: 900;
  font-size: 3rem;
  display: inline-block;
`;

export default function MainCounter({game_status, countdown}) {
  const [gameTime, setGameTime] = React.useState(60)
  const counter = () => setInterval(() => {
    setGameTime(gameTime - 0.1)   
  }, 100)
  React.useEffect(() => {
    game_status.isStarted && counter()
  }, [game_status.isStarted])
  return (
    <CounterContainer>
      {gameTime}
    </CounterContainer>
  )
}
