export const START_GAME = "START_GAME";
export const END_GAME = "END_GAME";
export const SHOW_RANDOM_GAME = "SHOW_RANDOM_GAME";
export const NEW_ANSWER = "NEW_ANSWER";
export const LOAD_NEW_GAME = "LOAD_NEW_GAME";
export const COUNTDOWN = "COUNTDOWN";
export const SHOW_HINT = "SHOW_HINT";
export const HIDE_HINT = "HIDE_HINT";


export const startGame = (difficulty) => ({
    type: START_GAME,
    difficulty,
})

export const resumeGame = () => ({
    type: START_GAME,
})

export const endGame = () => ({
    type: END_GAME,
})

export const showRandomGame = (game) => ({
    type: SHOW_RANDOM_GAME,
    game,
})

export const pushNewAnswer = (answer) => ({
    type: NEW_ANSWER,
    answer,
})

export const loadNewGame = () => ({
    type: LOAD_NEW_GAME,
})

export const showHint = (hints) => ({
    type: SHOW_HINT,
    hints,
})

export const hideHint = () => ({
    type: HIDE_HINT,
})


export const countdown = () => ({
    type: COUNTDOWN,
})
