import Table from "../../styled-components/Table";
import Typography from "../../styled-components/Typography";
import React from "react";
import { pickRandomGame } from "../../../global/fetch";
import { game_database } from "../../../global/const";
import moment from 'moment'

const game_difficulty = ["easy", "medium", "hard"];

export default function TableComponent({
  random_game,
  startGame,
  loadNewItem,
  endGame,
  showRandomGame,
  pushNewAnswer,
  showHint,
  hideHint,
  game_status,
}) {
  const [difficultyChoicePanel, showDifficultyChoicePanel] = React.useState(false)
  const [answerTimeStart, setAnswerTimeStart] = React.useState(false)
  const [hintCounter, setHintCounter] = React.useState(0)
  const [hints, setHints] = React.useState(false)
  const stop = () => {
    hideHint()
    setHintCounter(0)
    endGame()
  };
  const start = async (difficulty) => {
    difficulty && startGame(difficulty)
    loadNewItem()
    hideHint()
    setHintCounter(0)
    showRandomGame(await pickRandomGame(game_database))
  };
  const checkAnswer = async (answer) => {
    const answerTimeEnd = moment()
    const user_answer =
      answer === random_game.name
        ? {
            title: random_game.name,
            time: answerTimeEnd.diff(answerTimeStart),
            score: Number((1000/answerTimeEnd.diff(answerTimeStart)*10000*((10-hintCounter)/10)).toFixed()),
            correct: true,
            hints_used: hintCounter,
          }
        : {
            title: random_game.name,
            time: answerTimeEnd.diff(answerTimeStart),
            score: 0,
            correct: false,
            hints_used: hintCounter,
          };
    pushNewAnswer(user_answer)
    start()
  }
  const getHint = () => {
    showHint(...hints.splice(Math.floor(Math.random() * hints.length), 1))
    setHintCounter(hintCounter + 1)
  }
  React.useEffect(() => {
    !game_status.isLoading && setAnswerTimeStart(moment())
    !game_status.isStarted && showDifficultyChoicePanel(false)  
  }, [game_status])

  React.useEffect(() => {
    random_game && setHints(random_game.hints) 
  }, [random_game])

  return (
    <>
      <Table top>
        {game_status.isStarted && !game_status.isLoading && (
          <>
            {hintCounter < 3 && <Typography key={"key0011"} link onClick={() => getHint()}>get a hint</Typography>}
            <Typography key={"key0012"} link onClick={() => checkAnswer(false)}>skip</Typography>
            <Typography key={"key0013"} link onClick={stop}>quit</Typography>
          </>
        )}

        {(!game_status.isStarted && difficultyChoicePanel) && (
          <>
            <Typography key={"key0014"} link>set difficulty:{" "}</Typography>
            {game_difficulty.map((diffe, i) => (
            <Typography key={"key0011"+i} link onClick={() => start({i})}>{diffe}</Typography>))}
          </>
        )}

        {(!game_status.isStarted && !difficultyChoicePanel) && <Typography key={"key0015"} link onClick={() => showDifficultyChoicePanel(true)}>start a new challenge</Typography>}

        {game_status.isLoading && <Typography key={'key0016'} link>loading</Typography>}
      </Table>
      <Table bottom>
        {(game_status.isStarted && !game_status.isLoading && random_game.answers) ?
        random_game.answers.map((answer, i) => (
        <Typography key={"key0021"+i} answer onClick={(answer) => checkAnswer(answer.target.innerText)}>{answer}</Typography>))
        : ``}
      </Table>
    </>
  );
}
