import styled from "styled-components";
import { useState, useEffect, Component } from "react"
import moment from "moment"
import { countdown } from "../../redux/actions"

const CounterContainer = styled.div`
  text-transform: lowercase;
  font-weight: 900;
  font-size: 3rem;
  display: inline-block;
`;





export default function MainCounter({is_game_started, is_game_loading, game_counter}) {
  const [counter, setCounter] = useState(100)
  const [didMount, setDidMount] = useState(false)
  const [gameStarted, setGameStarted] = useState(false)
  useEffect(() => {
    const startCounting = (didMount && gameStarted && !is_game_loading) && setInterval(() => {
        setCounter(counter => counter - 1)
      }, 1000)
    return () => clearInterval(startCounting)
  }, [is_game_loading])

  useEffect(() => {
    didMount && (is_game_started ? setGameStarted(true) : setGameStarted(false))
  }, [is_game_started])

  useEffect(() => {
    setDidMount(true)
  }, [])

  return (
    <CounterContainer>
      {counter}
    </CounterContainer>
  )
}



