import styled from "styled-components";

const Score = styled.tr`
  font-family: Montserrat;
  font-size: clamp(1.2rem, 2.5vw, 1.6rem);
  letter-spacing: 0.05em;
  line-height: clamp(2rem, 2.5vw, 4rem);
`;

export default function ScoresTable({ data }) {
  const scores = data.slice(0, 15);
  return (
    <table>
      <thead>
        <Score>
          <th>No.</th>
          <th>Name</th>
          <th>Date</th>
          <th>Score</th>
        </Score>
      </thead>
      <tbody>
        {scores.map((score, i) => (
          <Score key={i}>
            <td>{i + 1}</td>
            <td>{score.Name}</td>
            <td>{score.Date}</td>
            <td>{score.Score}</td>
          </Score>
        ))}
      </tbody>
    </table>
  );
}
