import styled from "styled-components";
import React from "react"

const CounterContainer = styled.div`
  text-transform: uppercase;
  font-weight: 900;
  font-size: 3rem;
  display: inline-block;
`;

export default function MainCounter({game_status, countdown}) {
  const [gameTime, setGameTime] = React.useState(60)
  React.useEffect(() => {
    game_status.isStarted && setInterval(function(){
      setGameTime(gameTime - 1)   
    }, 1000)
  }, [game_status.isStarted])
  return (
    <CounterContainer>
      {gameTime}
    </CounterContainer>
  )
}
