import { combineReducers } from "redux"
import * as actions from "./actions"
import { games as source } from "../global/games"

const initialState = [...source.results].map((game,i) => game = {
    id: i+1,
    title: game.name,
    screenshots: [
        ...game.short_screenshots.slice(0, 3).map(screenshot => screenshot.image)
    ],
    hints: {
        genre: game.genres[game.genres.length-1].name,
        year: game.released,
    }
})

const game_database = (state = initialState, action) => action === actions.SELECT_GAME ? initialState : state

const game_status = (state = false, action) => action.type === actions.START_GAME ? true : false


export default combineReducers({
  game_database,
  game_status,
})
