import styled from "styled-components"

const ArrowContainer = styled.div`
    `

const Top = styled.div`
    height: 2px;
    width: 50px;
    background-color: var(--font-color);
    position: relative;
    top: 50%;
    ${props => props.left && (`
        transform: translateY(-50%) rotate(56deg);
        transform-origin: left;
        `
    )}
    ${props => props.right && (`
        transform: translateY(-50%) rotate(-56deg);
        transform-origin: left;
        `
    )}
    `

const Bottom = styled.div`
    height: 2px;
    width: 50px;
    background-color: var(--font-color);
    position: relative;
    top: 50%;
    ${props => props.left && (`
        transform: translateY(-50%) rotate(-56deg);
        transform-origin: left;
        `
    )}
    ${props => props.right && (`
        transform: translateY(-50%) rotate(56deg);
        transform-origin: left;
        `
    )}

    `

export default function ScreenshotArrow() {
    return (
        <ArrowContainer>
            <Top />
            <Bottom />
        </ArrowContainer>
    )
}
