import { connect } from "react-redux";
import Table from "../components/main/challenge/Table"
import { showHint, hideHint, pushNewAnswer, startGame, endGame, showRandomGame, loadNewItem } from "../redux/actions"

const mapState = state => ({
  is_game_started: state.is_game_started,
  is_game_loading: state.is_game_loading,
  game_status: state.game_status,
  random_game: state.random_game,
})

const mapDispatch  = dispatch => ({
  pushNewAnswer: (answer) => dispatch(pushNewAnswer(answer)),
  showRandomGame: (game) => dispatch(showRandomGame(game)),
  startGame: (difficulty) => dispatch(startGame(difficulty)),
  endGame: () => dispatch(endGame()),
  loadNewItem: () => dispatch(loadNewItem()),
  showHint: (hint) => dispatch(showHint(hint)),
  hideHint: () => dispatch(hideHint()),
})

export default connect(mapState, mapDispatch)(Table);
