import Layout from "../layout/Layout"
import ScreenshotViewer from "../containers/ScreenshotViewer"
import Table from "../containers/Table"
import Popup from "../components/styled-components/Popup"
import { useState } from "react"


export default function HomePage() {
  const [game_prepare, setGamePrepare] = useState(false)
  const [game_summary, setGameSummary] = useState(false)
  const [difficulty, setDifficulty] = useState(false)

  useEffect(() => {
    !game_prepare && setDifficulty(false)
  }, [game_prepare])

  return (
    <>
    <Layout title="Challenge">
        <ScreenshotViewer />
        <Table popupStart={(boolean) => setGamePrepare(boolean)} difficultyFromPopup={difficulty}/>
    </Layout>
    {game_prepare ? 
    <Popup game_prepare difficulty={(difficulty) => setDifficulty(difficulty)} /> : 
    game_summary && <Popup game_summary />}
    </>
  )
}


