import { combineReducers } from "redux"
import * as actions from "./actions"

const random_game = (state = "", action) => action.type === actions.SHOW_RANDOM_GAME ? action.game : state

// const user_score = (state = [], action) => action.type === actions.NEW_ANSWER ? [...state, action.answer] : state

const user_score = (state = [], action) => {
    if(action.type === actions.NEW_ANSWER) {
        console.log([...state, action.answer])
        return [...state, action.answer]
    } else {
        return state
    }
}

const hints_array = (state = [], action) => {
    switch(action.type) {
        case actions.SHOW_HINT: {
            console.log([...state, action.hint])
            return [...state, action.hint]
        }
        case actions.HIDE_HINT: return []
        default: return state
    }
}

const game_counter = (state = 60, action) => {
    switch(action.type) {
        case actions.COUNTDOWN: return state - 1
        case actions.END_GAME: return 60
        default: return state
    }
}

const is_game_started = (state = false, action) => {
    switch(action.type) {
        case actions.START_GAME: return true
        case actions.END_GAME: return false
        default: return state
    }
}

const is_game_loading = (state = false, action) => {
    switch(action.type) {
        case actions.LOAD_NEW_ITEM: return true
        case actions.RESUME_GAME: return false
        default: return state
    }
}


export default combineReducers({
  is_game_started,
  random_game,
  user_score,
  game_counter,
  hints_array,
  is_game_loading,
})
