import Table from "../../styled-components/Table";
import Typography from "../../styled-components/Typography";
import React from "react";
import { pickRandomGame } from "../../../global/fetch";
import { game_database } from "../../../global/const";
import moment from 'moment'

const game_difficulty = ["easy", "medium", "hard"];

export default function TableComponent({
  game_status,
  random_game,
  startGame,
  pauseGame,
  endGame,
  showRandomGame,
  pushNewAnswer
}) {
  let answerTimeStart = ""
  let answerTimeEnd = ""
  const possibleAnswersLoaded = () => {
    answerTimeStart = new moment()
  }
  const [difficultyChoicePanel, showDifficultyChoicePanel] = React.useState(false);
  const stop = () => {
    showDifficultyChoicePanel(false);
    endGame();
  };
  const start = async (difficulty) => {
    pauseGame();
    showRandomGame(await pickRandomGame(game_database));
    difficulty && startGame(difficulty);
  };
  const checkAnswer = (answer) => {
    answerTimeEnd = new moment()
    const user_answer =
      answer === random_game.name
        ? {
            title: random_game.name,
            time: moment.duration(answerTimeStart.diff(answerTimeEnd)),
            score: 2000,
            correct: true,
          }
        : {
            title: random_game.name,
            time: moment.duration(answerTimeStart.diff(answerTimeEnd)),
            score: 0,
            correct: false,
          };
    pushNewAnswer(user_answer);
    start();
  };
  return (
    <>
      <Table top>
        {(game_status.isRunning && !game_status.isLoading) && (
          <>
            <Typography key={"key0011"} link href="/">get a hint</Typography>
            <Typography key={"key0012"} link onClick={() => checkAnswer(false)}>skip</Typography>
            <Typography key={"key0013"} link onClick={stop}>quit</Typography>
          </>
        )}

        {(!game_status.isRunning && difficultyChoicePanel) && (
          <>
            <Typography key={"key0014"} link>set difficulty:{" "}</Typography>
            {game_difficulty.map((diff, i) => (
            <Typography key={"key0011"+i} link onClick={() => start({i})}>{diff}</Typography>))}
          </>
        )}

        {(!game_status.isRunning && !difficultyChoicePanel) && <Typography key={"key0015"} link onClick={() => showDifficultyChoicePanel(true)}>start a new challenge</Typography>}

        {game_status.isLoading && <Typography key={'key0016'} link>loading</Typography>}
      </Table>
      <Table bottom>
        {(game_status.isRunning && !game_status.isLoading && random_game.answers) ?
        random_game.answers.map((answer, i) => (
        <Typography key={"key0021"+i} onLoad={possibleAnswersLoaded} answer onClick={(answer) => checkAnswer(answer.target.innerText)}>{answer}</Typography>))
        : ``}
      </Table>
    </>
  );
}
