import styled from "styled-components";
import React from "react"
import moment from "moment"
import { countdown } from "../../redux/actions"

const CounterContainer = styled.div`
  text-transform: lowercase;
  font-weight: 900;
  font-size: 3rem;
  display: inline-block;
`;

export default function MainCounter({isGameStarted, game_status, main_counter}) {
  //const [, ] = React.useState(false)
  const startCounting = () => async (dispatch) => setInterval(() => {
      dispatch(countdown())
    }, 1000)
  startCounting()
  return (
    <CounterContainer>
      {main_counter} s
    </CounterContainer>
  )
}


