import styled from "styled-components";
import { useState, useEffect } from "react"


const CounterContainer = styled.div`
  text-transform: lowercase;
  font-weight: 900;
  font-size: 3rem;
  display: inline-block;
`;


export default function MainCounter({game_status}) {
  const [counter, setCounter] = useState(100)
  const [didMount, setDidMount] = useState(false)

  useEffect(() => {
    !game_status.isStarted && setCounter(100)
    const startCounting = (didMount && game_status.isStarted && !game_status.isLoading) && setInterval(() => {
        setCounter(counter => counter - 0.1)
      }, 100)
    return () => clearInterval(startCounting)
  }, [game_status])                                         

  useEffect(() => {
    setDidMount(true)
  }, [])

  return (
    <CounterContainer>
      {counter.toFixed(3)}
    </CounterContainer>
  )
}



