import styled from "styled-components";
import { useState, useEffect } from "react"


const CounterContainer = styled.div`
  text-transform: lowercase;
  font-weight: 900;
  font-size: 3rem;
  display: inline-block;
`;


export default function MainCounter({is_game_started, is_game_loading}) {
  const [counter, setCounter] = useState(100)
  const [didMount, setDidMount] = useState(false)
  var startCounting
  useEffect(() => {
    startCounting = (didMount && is_game_started && !is_game_loading) && setInterval(() => {
        setCounter(counter => counter - 0.1)
      }, 100)
    return () => clearInterval(startCounting)
  }, [is_game_loading])                                          // merge reducer into obj

  useEffect(() => {
     if(!is_game_started) {
       console.log(startCounting)
      return () => clearInterval(startCounting)
     } 
  }, [is_game_started])

  useEffect(() => {
    setDidMount(true)
  }, [])

  return (
    <CounterContainer>
      {counter.toFixed(3)}
    </CounterContainer>
  )
}
