import styled from "styled-components";
import { useState, useEffect } from "react";

const CounterContainer = styled.div`
  text-transform: lowercase;
  font-weight: 900;
  font-size: 3rem;
  display: inline-block;
  & span {
    font-size: 2rem;
  }
`;

export default function MainCounter({ game_status, endGame }) {
  const [counter, setCounter] = useState(60);
  const [didMount, setDidMount] = useState(false);

  useEffect(() => {
    !game_status.isStarted && setCounter(60);
    const startCounting =
      didMount &&
      game_status.isStarted &&
      !game_status.isLoading &&
      setInterval(() => {
        setCounter((counter) => counter - 0.1);
      }, 100);
    return () => clearInterval(startCounting);
  }, [game_status]);

  useEffect(() => counter <= 0 && endGame(), [counter]);

  useEffect(() => {
    setDidMount(true);
  }, []);

  return (
    <CounterContainer>
      {game_status.isStarted && counter > 0 && `00:${counter.toFixed()} s`}
      {/* <span>{game_status.isStarted && counter > 0 && `${counter.toFixed(3).replace(/\d+\./,'.')} s`}</span> */}
    </CounterContainer>
  );
}

