import styled from "styled-components";
import { useState, useEffect, Component } from "react"
import moment from "moment"
import { countdown } from "../../redux/actions"

const CounterContainer = styled.div`
  text-transform: lowercase;
  font-weight: 900;
  font-size: 3rem;
  display: inline-block;
`;

export default class MainCounter extends Component {
  constructor(props) {
    super(props);
  }
  state = 60
  componentDidMount() {
    if(this.props.is_game_started && !this.props.is_game_loading) {
      console.log('interval begin')
      this.startCounting = setInterval(() => {
        this.setState(this.state - 1)
      }, 1000)
    }
    if(this.props.is_game_started && this.props.is_game_loading) {
      console.log('interval end')
      clearInterval(this.startCounting)
    }
  }
  render() {
    return (
      <CounterContainer>
        {this.state}
      </CounterContainer>
    )
  }
}



