import styled from "styled-components"

const ScreenshotContainer = styled.div`
    background-color: var(--black);
    width: calc(85% - 4rem);
    height: 0;
    padding: 0.5rem;
    padding-bottom: calc(47.75% - (0.5 * 4rem));
    border-radius: 5px;
    box-shadow: 0 0 25px #FFF;
    margin: 0 auto;
    display: flex;

    & > img {
        width: 100%;
        margin: auto;
        position: absolute;
    }
    `

export default function Screenshot({src}) { 
    return (
        <ScreenshotContainer>
            <img src={src} alt="screenshot_img" /> 
            <img src={src} alt="screenshot_img" /> 
            <img src={src} alt="screenshot_img" /> 
        </ScreenshotContainer>
    )
}
