import { combineReducers } from "redux"
import * as actions from "./actions"
import { games as source } from "../global/games"

const game_database = [...source.results].map((game,i) => game = {
    id: i+1,
    title: game.name,
    screenshots: [
        ...game.short_screenshots.slice(0, 3).map(screenshot => screenshot.image)
    ],
    hints: {
        genre: game.genres[game.genres.length-1].name,
        year: game.released,
    }
})

// const game_database = (state = initialState, action) => action === actions.SELECT_GAME ? initialState : state

const game_status = (state = false, action) => action.type === actions.START_GAME ? true : action.type === actions.STOP_GAME ? false : state

const random_game = (state = "", action) => {
    switch(action.type) {
        case actions.START_GAME: {
            let randomIndex = Math.floor(Math.random()*game_database.length);
            let game = game_database[randomIndex];
            game_database.splice(randomIndex, 1);
            console.log(game_database);
            return game;
        }
        default: return state
    } 
}

export default combineReducers({
  game_status,
  random_game,
})
