import styled from "styled-components";
import React from "react"
import {countdown} from "../../redux/actions"
import moment from "moment"

const CounterContainer = styled.div`
  text-transform: uppercase;
  font-weight: 900;
  font-size: 3rem;
  display: inline-block;
`;

export default function MainCounter({game_status, main_counter}) {
  const [gameTime, setGameTime] = React.useState(60)
  let x = 60
  const task = () => {
    x--
    setGameTime(x)
  }
  const runCounting = setInterval(task, 1000);
  const stopCounting = () => clearInterval(runCounting)
  return (
    <CounterContainer>
      {gameTime > 40 ? gameTime : stopCounting()}
    </CounterContainer>
  )
}


