import Layout from "../layout/Layout"
import ScreenshotViewer from "../containers/ScreenshotViewer"
import Table from "../containers/Table"
import { connect } from "react-redux";
import { countdown } from "../redux/actions"
import { useState, useEffect } from "react"

const mapState = state => ({
  game_status: state.game_status,
  main_counter: state.main_counter,
})

const mapDispatch  = dispatch => ({
  countdown: () => dispatch(countdown()),
})

function HomePage({game_status, main_counter, countdown}) {
//  const [number, setNumber] = useState(60)
  const counter = async () => {
    await countdown()
  }
  const interv = setInterval(counter, 1000)
  return (
    <Layout title="Challenge">
        <ScreenshotViewer />
        <Table />
    </Layout>
  )
}

export default connect(mapState, mapDispatch)(HomePage);



