import styled from "styled-components"

const ScreenshotContainer = styled.div`
    background-color: var(--black);
    width: calc(85% - 4rem);
    height: 0;
    padding-bottom: calc(47.75% - (0.5 * 4rem));
    border-radius: 5px;
    box-shadow: 0 0 25px #FFF;
    margin: 0 auto;
    display: flex;
    position: relative;

    & > img {
        width: 97%;
        transform: translate(1.5%, 2%);
        margin: auto;
        position: absolute;
        border-radius: 3px;
    }
    `

export default function Screenshot({src}) { 
    return (
        <ScreenshotContainer>
            <img src="https://media.rawg.io/media/games/618/618c2031a07bbff6b4f611f10b6bcdbc.jpg" alt="screenshot_img" /> 
            <img src="https://media.rawg.io/media/screenshots/1ac/1ac19f31974314855ad7be266adeb500.jpg" alt="screenshot_img" /> 
            <img src="https://media.rawg.io/media/screenshots/6a0/6a08afca95261a2fe221ea9e01d28762.jpg" alt="screenshot_img" /> 
        </ScreenshotContainer>
    )
}
