import styled from "styled-components";
import { useState, useEffect } from "react"
import moment from "moment"
import { countdown } from "../../redux/actions"

const CounterContainer = styled.div`
  text-transform: lowercase;
  font-weight: 900;
  font-size: 3rem;
  display: inline-block;
`;

export default function MainCounter({is_game_started, is_game_loading, game_counter}) {
  const [counter, setCounter] = useState(30)
  const [didMount, setDidMount] = useState(false)
  let countingInterval
  const startCounting = () => {
    countingInterval = setInterval(() => {
      setCounter(counter => counter - 1)
    }, 1000)
  } 
  const stopCounting = () => clearInterval(countingInterval)
  useEffect(() => {
    didMount && (is_game_loading ? stopCounting() : startCounting())
  }, [is_game_loading])
  useEffect(() => {
    if(didMount) {
      stopCounting()
      setCounter(60)
    }
  }, [is_game_started])
  useEffect(() => {
    setDidMount(true)
  }, [])
  return (
    <CounterContainer>
      {counter}
    </CounterContainer>
  )
}


