import styled from "styled-components";
import React from "react"
import {countdown} from "../../redux/actions"

const CounterContainer = styled.div`
  text-transform: uppercase;
  font-weight: 900;
  font-size: 3rem;
  display: inline-block;
`;

export default function MainCounter({game_status, main_counter}) {
  const [gameTime, setGameTime] = React.useState(60)
  const startTimestamp = moment().startOf("day")
  const startTimer = () => {
      console.log(startTimestamp)
      setInterval(function() {
          startTimestamp.add(100, 'milisecond');
      }, 100);
  }
  React.useEffect(() => {
    game_status.isStarted ? 
    startTimer()
    : console.log('game over')
  }, [game_status.isStarted])
  React.useEffect(() => {
    game_status.isRunning ? console.log('running') : game_status.isLoading && console.log('loading') 
  }, [game_status.isRunning])
  return (
    <CounterContainer>
      {startTimestamp.format('HH:mm:ss')}
    </CounterContainer>
  )
}
