import Table from "../../styled-components/Table";
import Typography from "../../styled-components/Typography";
import React from "react"
import { pickRandomGame } from "../../../global/fetch"
import { games as source } from "../../../global/games"

const game_database = [...source.results].map(game => game = {
    id: game.id,
})

console.log(game_database)

export default function TableTop({ game_status, startGame, stopGame, generateGame, getRandomGame }) {
  const [difficultyChoice, showDifficultyChoice] = React.useState(false)
  const skip = () => generateGame()
  const stop = () => {
    showDifficultyChoice(false);
    return stopGame();
  }
  const start = async () => {
    const randomik = await pickRandomGame(game_database)
    getRandomGame(randomik)
    startGame(2)
  }
  return (
    <Table top>
      {game_status ? (
        <>
          <Typography key={Math.random() * 10000} link href="/">get a hint</Typography>
          <Typography key={Math.random() * 10000} link onClick={skip}>skip</Typography>
          <Typography key={Math.random() * 10000} link onClick={stop}>quit</Typography>
        </>
      ) : difficultyChoice ? (
        <>
        <Typography key={Math.random() * 10000} link >set difficulty: </Typography>
        <Typography key={Math.random() * 10000} link onClick={() => startGame(0)}>easy</Typography>
        <Typography key={Math.random() * 10000} link onClick={() => startGame(1)}>medium</Typography>
        <Typography key={Math.random() * 10000} link onClick={start}>hard</Typography>
        </>
      ) : <Typography key={Math.random() * 10000} link onClick={() => showDifficultyChoice(true)}>start a new challenge</Typography>}
    </Table>
  );
}



















