import Table from "../../../styled-components/Table";
import Typography from "../../../styled-components/Typography";
import { useState, useEffect, Fragment } from "react";
import { pushNewAnswer } from "../../../../redux/actions";

export default function GameNavigatorTop({ 
  game_is_started, 
  game_is_loading,
  changePopup,
  endGame, 
  showHint, 
  game_database,
  loadNewQuestion,
  startLoading,
  random_game,
  user_score,
  setHintCounter,
  hintCounter,
  pushNewAnswer,
 }) {
  const [hints, setHints] = useState(false)
  const handleClickHint = () => {
    showHint(...hints.splice(Math.floor(Math.random() * hints.length), 1))
    setHintCounter()
  }
  const handleClickSkip = () => {
    pushNewAnswer({
      score: 0,
      hints_used: hintCounter,
      skip: true,
    })
    startLoading()
    loadNewQuestion(game_database)
  }
  useEffect(() => {
    random_game && setHints(random_game.hints)
  }, [random_game])
  useEffect(() => {
    user_score && user_score.length > 0 && !game_is_started && changePopup('summary')
  }, [game_is_started])
  return (
      <Table top>    
        {
        !game_is_started 
          ? <Typography link onClick={() => changePopup('userdata')}>start a new challenge</Typography>
          : game_is_loading 
            ? <Typography link>loading</Typography>
            : (
              <Fragment>
                {hintCounter < 3 && 
                <Typography link onClick={() => handleClickHint()}>get a hint</Typography>}
                <Typography link onClick={() => handleClickSkip()}>skip</Typography>
                <Typography link onClick={() => endGame()}>quit</Typography>
              </Fragment>
            )
        }
      </Table>
  )
}
