import styled from "styled-components";
import React from "react";
import Typography from "./Typography";

const Background = styled.div`
  height: 100vh;
  width: 100vw;
  top: 0;
  position: absolute;
  background: rgba(0, 0, 0, .75);
  ${props => props.visibility ? `opacity: 1; visibility: visible;` : `opacity: 0; visibility: hidden;` }
`
const PopupContainer = styled.div`
  width: min(75rem, 95%);
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: rgba(39, 39, 39, 1);
  border-radius: 3px;
`
const TopBar = styled.div`
  position: relative;
  padding: 2rem;
  text-align: center;
  background: rgba(100, 100, 100, 1);
  border-radius: 3px 3px 0 0;
`
const Close = styled.div`
  position: absolute;
  top: 50%;
  right: 5%;
  transform: translate(0, -50%);
  font-size: 3rem;
`
const Content = styled.div`
  padding: 3rem;
  display: flex;
  flex-direction: column;
  align-items: center;
`
const TextInput = styled.input`
  background: black;
  color: white;
  width: 55%;
  font-size: 3rem;
  padding: 1rem;
  border: none;
  text-align: center;
  border-radius: 3px;
  font-family: 'Ruda';
`
const TextInputHint = styled.p`
  color: red;
  font-size: 1.2rem;
  padding: 1rem;
`
const WelcomeText = styled.div`
  color: var(--white);
  font-size: clamp(1.2rem, 2.5vw, 1.6rem);
  text-align: left;
  width: 100%;
  padding-bottom: 3rem;
`

export default function(props) {
  const [visibility, setVisibility] = React.useState(true)
//   React.useEffect(() => {
//     props.show ? setVisibility(true) : setVisibility(false)
//   },[props.show])  
  return (
    <Background visibility={visibility}>
      <PopupContainer>
        <TopBar>
          <Close onClick={() => setVisibility(false)}>&times;</Close>
          <Typography hint>hello, gamergeek</Typography>
        </TopBar>
        <Content>
            <WelcomeText>Let’s see how good you are! Starting from your name...</WelcomeText>
            <TextInput type="text" />
            <TextInputHint>Wrong nickname</TextInputHint>
            <button>Play!</button>
        </Content>
      </PopupContainer>
    </Background>
  );
}
