import styled from "styled-components";
import React from "react"
import {countdown} from "../../redux/actions"
import moment from "moment"

const CounterContainer = styled.div`
  text-transform: uppercase;
  font-weight: 900;
  font-size: 3rem;
  display: inline-block;
`;

export default function MainCounter({game_status, main_counter}) {
  React.useEffect(() => {
    if(game_status.isStarted) {
      let startingValue = 60
      const DIV = document.getElementById('counter')
      const task = () => {
        startingValue--
        DIV.innerText = startingValue
        startingValue === 0 && stopCounting()
      }
      const runCounting = setInterval(task, 1000)
      const stopCounting = () => clearInterval(runCounting)
    }
  },[game_status.isStarted])
  return (
    <CounterContainer>
      <div id='counter'></div>
    </CounterContainer>
  )
}


