import styled from "styled-components"

const TableContainer = styled.div`
    ${props => props.top && (
        `
        background: linear-gradient(360deg, rgba(80, 80, 80, 0.25) 15%, rgba(196, 196, 196, 0) 88%);
        height: 10rem;
        clip-path: polygon(235px 0, 965px 0, 100% 100%, 0 100%);
        `
    )}
    ${props => props.bottom && (
        `
        background: linear-gradient(180deg, rgba(80, 80, 80, 0.25) 15%, rgba(196, 196, 196, 0) 88%);
        height: 12.5rem;
        `
    )}
    ${props => props.hint && (
        `
        position: absolute;
        bottom: 0;
        background: linear-gradient(360deg, rgba(94, 90, 0, 0.25) 15%, rgba(255, 255, 255, 0) 88%);
        height: 12.5rem;
        width: 100%;
        `
    )}
    border-radius: 5px;
    `

export default function Table({...props}) {
    return (
        <TableContainer {...props} />
    )
}
