import { connect } from "react-redux";
import Popup from "../components/main/challenge/containers/Popup"
import { 
    changePopup, 
    loadNewQuestion, 
    newGameDatabase,
    startGame,
    clearUserScore
 } from "../redux/actions"

const mapState = state => ({
    game_database: state.game_database,
    show_popup: state.show_popup,
  })

const mapDispatch = dispatch => ({
    changePopup: (popup_name) => dispatch(changePopup(popup_name)),
    startGame: () => dispatch(startGame()),
    newGameDatabase: (db) => dispatch(newGameDatabase(db)),
    loadNewQuestion: (db) => dispatch(loadNewQuestion(db)),
    clearUserScore: () => dispatch(clearUserScore()),
})

export default connect(mapState, mapDispatch)(Popup);
