import styled from "styled-components";
import { useState, useEffect } from "react"
import moment from "moment"
import { countdown } from "../../redux/actions"

const CounterContainer = styled.div`
  text-transform: lowercase;
  font-weight: 900;
  font-size: 3rem;
  display: inline-block;
`;

export default function MainCounter({is_game_started, is_game_loading, game_counter}) {
  const [counter, setCounter] = useState("")
  const [didMount, setDidMount] = useState(false)
  let countingInterval
  const startCounting = () => {
    countingInterval = setInterval(function() {
      setCounter(counter => counter - 1)
      console.log('decreasing counter')
    }, 1000)
  } 
  const stopCounting = () => {
    clearInterval(countingInterval)
    console.log('clearing interval')
  }
  useEffect(() => {
    didMount && (is_game_loading ? stopCounting() : startCounting())
  }, [is_game_loading])
  useEffect(() => {
    if(didMount) {
      stopCounting()
      setCounter(60)
    }
  }, [is_game_started])
  useEffect(() => {
    setDidMount(true)
  }, [])
  return (
    <CounterContainer>
      {counter}
    </CounterContainer>
  )
}


