import styled from "styled-components";
import React from "react"
import moment from "moment"

const CounterContainer = styled.div`
  text-transform: lowercase;
  font-weight: 900;
  font-size: 3rem;
  display: inline-block;
`;

export default function MainCounter({isGameStarted, main_counter, countdown}) {
  //const [, ] = React.useState(false)
  const startCounting = () => {
    countdown()
  }
  const counterStart = isGameStarted ? setInterval(startCounting, 1000) : clearInterval(startCounting)

  return (
    <CounterContainer>
      {main_counter} s
    </CounterContainer>
  )
}


