export const GENERATE_GAME = "GENERATE_GAME";
export const START_GAME = "START_GAME";
export const STOP_GAME = "STOP_GAME";

export const generateGame = () => ({
    type: GENERATE_GAME,
})

export const startGame = (difficulty) => ({
    type: START_GAME,
    difficulty,
})

export const stopGame = () => ({
    type: STOP_GAME,
})
