import styled from "styled-components";
import React from "react"

const CounterContainer = styled.div`
  text-transform: uppercase;
  font-weight: 900;
  font-size: 3rem;
  display: inline-block;
`;

export default function MainCounter({game_status}) {
  const [gameTime, setGameTime] = React.useState("")
  React.useEffect(() => {
    if (game_status.isStarted) {
      setGameTime(60)
      setInterval(function(){
        game_status.isRunning && setGameTime(gameTime - 1)   
        console.log('tik')
      }, 1000)
    }
  }, [game_status.isStarted])
  return (
    <CounterContainer>
      {gameTime}
    </CounterContainer>
  )
}
