import Container from "../../styled-components/Container"
import Screenshot from "../../styled-components/Screenshot"
import ScreenshotArrow from "../../styled-components/ScreenshotArrow"
import { games as source } from "../../../global/games"
import React from "react"

const games = [...source.results].map((game,i) => game = {
    id: i+1,
    title: game.name,
    screenshots: [
        ...game.short_screenshots.slice(0, 3).map(screenshot => screenshot.image)
    ],
    hints: {
        genre: game.genres[game.genres.length-1].name,
        year: game.released,
    }
})

const screenshots = [...games[2].screenshots]

console.log(games)

export default function Challenge() {
const [screenshotIndex, setIndex] = React.useState(0)
const previousClick = () => screenshotIndex === 0 ? setIndex(2) : setIndex(screenshotIndex - 1)
const nextClick = () => screenshotIndex === 2 ? setIndex(0) : setIndex(screenshotIndex + 1)
    return (
        <Container type="standard" width="min(95rem, 95%)" flex>
            <ScreenshotArrow left onClick={previousClick}/>
            <Screenshot src={screenshots[screenshotIndex]} />
            <ScreenshotArrow right onClick={nextClick}/>
        </Container>
    )
}
