import styled from "styled-components";
import { useState, useEffect } from "react"


const CounterContainer = styled.div`
  text-transform: lowercase;
  font-weight: 900;
  font-size: 3rem;
  display: inline-block;
`;


export default function MainCounter({game_status, endGame}) {
  const [counter, setCounter] = useState(10)
  const [didMount, setDidMount] = useState(false)

  useEffect(() => {
    !game_status.isStarted && setCounter(10)
    const startCounting = (didMount && game_status.isStarted && !game_status.isLoading) && setInterval(() => {
        counter > 0 && setCounter(counter => counter - 0.1)
      }, 100)
    return () => clearInterval(startCounting)
  }, [game_status])                                         

  useEffect(() => {
    if(counter <= 0) {
      return () => endGame()
    } 
  }, [counter])

  useEffect(() => {
    setDidMount(true)
  }, [])

  return (
    <CounterContainer>
      {game_status.isStarted && counter.toFixed(3)}
    </CounterContainer>
  )
}



