import styled from "styled-components";
import React from "react";
import Typography from "./Typography";

const Background = styled.div`
  height: 100vh;
  width: 100vw;
  top: 0;
  position: absolute;
  background: rgba(0, 0, 0, .75);
`
const PopupContainer = styled.div`
  width: min(75rem, 95%);
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: rgba(39, 39, 39, 1);
  border-radius: 3px;
`
const TopBar = styled.div`
  position: relative;
  padding: 2rem;
  text-align: center;
  background: rgba(139, 139, 139, 1);
  border-radius: 3px 3px 0 0;
`
const Close = styled.div`
  position: absolute;
  top: 50%;
  right: 5%;
  transform: translate(0, -50%);
  font-size: 3rem;
`
const Content = styled.div`
  padding: 3rem;
`
const TextInput = styled.input`
  background: black;
  color: white;
  width: 55%;
  font-size: 3rem;
  padding: 1rem;
`
const TextInputHint = styled.p`
  color: red;
  font-size: 1.2rem;
  padding: 1rem;
`

export default function () {
  return (
    <Background>
      <PopupContainer>
        <TopBar>
          <Close>&times;</Close>
          <Typography hint>hello, gamergeek</Typography>
        </TopBar>
        <Content>
            <Typography p>Letâ€™s see how good you are! Starting from your name...</Typography>
            <TextInput type="text" />
            <TextInputHint />
            <button>Play!</button>
        </Content>
      </PopupContainer>
    </Background>
  );
}
