import React from "react"
import {countdown} from "../../redux/actions"
import moment from "moment"

const CounterContainer = styled.div`
  text-transform: uppercase;
  font-weight: 900;
  font-size: 3rem;
  display: inline-block;
`;

export default function MainCounter({game_status, main_counter, countdown}) {
  React.useEffect(() => {
    if(game_status.isStarted) {
      const runCounting = setInterval(() => {
        game_status.isRunning && countdown()
      }, 1000)
    }
  },[game_status.isStarted])
  return (
    <CounterContainer>
      {main_counter} s
    </CounterContainer>
  )
}


