import styled from "styled-components"

const ArrowContainer = styled.div`
    position: relative;
    width: 4rem;
    &:hover {
        cursor: pointer;
    }
    `

const Top = styled.div`
    height: 2px;
    width: 4rem;
    background-color: var(--font-color);
    position: absolute;
    top: 53.3%;
    ${props => props.left && (`
        transform: translateY(-50%) rotate(56deg);
        transform-origin: left;
        `
    )}
    ${props => props.right && (`
        transform: rotate(-56deg);
        `
    )}
    `

const Bottom = styled.div`
    height: 2px;
    width: 4rem;
    background-color: var(--font-color);
    position: absolute;
    top: 46%;
    ${props => props.left && (`
        transform: translateY(-50%) rotate(-56deg);
        transform-origin: left;
        `
    )}
    ${props => props.right && (`
        transform: rotate(56deg);
        `
    )}
    `

export default function ScreenshotArrow(props) {
    return (
        <ArrowContainer>
            <Top {...props} />
            <Bottom {...props} />
        </ArrowContainer>
    )
}
