import Table from "../../styled-components/Table";
import Typography from "../../styled-components/Typography";
import React from "react"

export default function TableTop({ game_status, startGame }) {
  const [difficultyChoice, showDifficultyChoice] = React.useState(false)
  return (
    <Table top>

      {difficultyChoice ? (
        <>
        <Typography link>set difficulty: </Typography>
        <Typography link>easy</Typography>
        <Typography link>medium</Typography>
        <Typography link>hard</Typography>
        </>
      ) : (
        <Typography link onClick={() => showDifficultyChoice(true)}>start a new challenge</Typography>
      )}

      {game_status && (
        <>
          {showDifficultyChoice(false)}
          <Typography link href="/">
            get a hint
          </Typography>
          <Typography link href="/">
            skip
          </Typography>
        </>
      )}
    </Table>
  );
}


